import React from 'react'
import "./Createpost.css"

export default function Createpost() {
    const loadfile = (event)=>{
        var output = document.getElementById('output');
    output.src = URL.createObjectURL(event.target.files[0]);
    output.onload = function() {
      URL.revokeObjectURL(output.src);
    }
}
  return (
    <div className="createPost">

        <div className="post-header">
            <h4 style={{margin:"3px auto"}}>Create new Post</h4>
            <button id="post-btn">Share</button>
        </div>

        <div className="main-div">
        <img id="output" src="https://upload.wikimedia.org/wikipedia/commons/thumb/6/6b/Picture_icon_BLACK.svg/224px-Picture_icon_BLACK.svg.png?20180309172929"/>
            <input type="file" accept="image/*" onChange={(event)=>{loadfile(event)}} />
        </div>

        <div className="details">

            <div className="card-header">
                <div className="card-pic">
                    <img src="https://plus.unsplash.com/premium_photo-1697729758146-9aa25d423094?q=80&w=1968&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" alt="" />
                    <h5>Giriraj</h5>
                </div>
            
                </div>
                <textarea 
                type="text" placeholder='Write a caption'></textarea>
        </div>

    </div>
  )
}
