import React,{useEffect}  from 'react'
import "./Home.css"
import {useNavigate} from 'react-router-dom';

export default function Home() {
  
  const navigate = useNavigate();

  useEffect(() => {
    const token=localStorage.getItem("jwt");
    if(!token){
      navigate("./signin");
    }

    //fetching all posts
    fetch("http://localhost:5004/allposts",{
      headers:{
        "Authorization":"Bearer "+token
      }
    }).then(res => res.json())
    .then()

  }, [])
  

  return (
    <div className="home">
      {/*card*/}
      <div className="card">
      {/* card-header */}
    <div className="card-header">
      <div className="card-pic">
        <img src="https://images.unsplash.com/flagged/photo-1570612861542-284f4c12e75f?q=80&w=870&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" alt="" />
      </div>
      <h5>Ramesh</h5>

    </div>
    {/* cardimahe */}
    <div className="card-image">  
    <img src="https://images.unsplash.com/photo-1446889727648-8c23e3039b24?w=1000&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8Mnx8c3F1YXJlJTIwaW1hZ2V8ZW58MHx8MHx8fDA%3D" alt="" />
    </div>

    {/* card content */}
    <div className="card-content">
    <span className="material-symbols-outlined">
favorite
</span>
    <p>1 Like</p> 
    <p>This is amazing</p>
    
    </div>
      <div className="add-comment">
      <span className="material-symbols-outlined">
mood
</span>
<input type="text" placeholder='add a comment' />
<button className='comment'>Post</button>
      </div>

    </div>
    </div>
  )
}
